    int numberOfAlternatingGroups(vector<int>& c, int k) {
    int n=c.size();
        for(int i=0;i<n;i++){
            c.push_back(c[i]);
        }
        int ans=0;
        int st=0;


        int s=1;
        for(int e=1;e<n+k-1;e++){
            if(c[e]!=c[e-1]){
                s++;
            }
            else{
                st=e;
                s=1;
            }

            if(s==k){
                ans++;
                st++;
                s--;
            }
        }
        return ans;
    }
