class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i = 0, ans = 0;
        int n = colors.size();
        while (i < n) {
            int p = 1;
            while (p < n + 1 && colors[(i + p - 1)%n] != colors[(i + p)%n]) p++;
            if (p > n) {
                ans = n;
                break;
            }
            ans += max(0, min(n - i, p - k + 1));
            i += p;
        }
        return ans;
    }
};
