class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors+=colors
        a=[]
        p=0
        prev=-1
        for i in colors:
            if(prev==i):
                p=1
            else:
                p+=1
            a.append(p)
            prev=i
        # print(a)
        ans=0
        for i in range(n,2*n):
            if(a[i]>=k):
                ans+=1
        return ans
