class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int x) {
        int n=colors.size();
        vector<int> pref(n+1,0);
        for(int i=0;i<n;i++){
            pref[i+1]=pref[i];
            int j=(i-1+n)%n;
            int k=(i+1)%n;
            if(colors[i]!=colors[j] && colors[i]!=colors[k]) pref[i+1]++;
        }

        int cnt=0;
        for(int i=0;i<n;i++){
            int j=i+1;
            int k=i+x-2;
            int t=0;
            if(k<n){
                t+=(pref[k+1]-pref[j]);
            }
            else {
                t+=pref[n]-pref[j];
                k%=n;
                t+=pref[k+1]-pref[0];
            }
            if(t+2==x) cnt++;
        }

        return cnt;
    }
};
