class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
         int c=0; 
        int n=colors.size(); 
        if(k==1){ 
            return n; 
        } 
        int altLen=1; 
        int i=1; 
        while(i<n+k-1){ 
            int now=i; 
            int before=i-1; 

            if(now>=n){ 
                now-=n; 
            } 
            if(before>=n){ 
                before-=n; 
            } 
            if(colors[now]!=colors[before]){ 
                ++altLen;
            }else{ 
                altLen=1; 
            } 
            if(altLen>=k){ 
                ++c; 
            } 
            ++i; 
        } 
        return c; 
    }
};
