class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        queue<int> window;

        for (int i = 0; i < n + k - 1; ++i) {
            int currentColor = colors[i % n];

            if (!window.empty() && currentColor == window.back()) {
                while (!window.empty()) {
                    window.pop();
                }
            }

            window.push(currentColor);

            if (window.size() == k) {
                ++count;
                window.pop();
            }
        }

        return count;
    }
};
