class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        int altCount = 0;

        for (int j = 0; j < k - 1; ++j) {
            if (colors[j] != colors[j + 1]) {
                altCount++;
            }
        }

        if (altCount == k - 1) {
            count++;
        }

        for (int i = k; i < n + k - 1; ++i) {
            int prevIdx = (i - k) % n;
            int nextPrevIdx = (i - k + 1) % n;
            int lastIdx = (i - 1) % n;
            int currentIdx = i % n;

            if (colors[prevIdx] != colors[nextPrevIdx]) {
                altCount--;
            }

            if (colors[lastIdx] != colors[currentIdx]) {
                altCount++;
            }

            if (altCount == k - 1) {
                count++;
            }
        }

        return count;
    }
};
