class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int l{},r{k-1},ans{};
        for(int i=0;i<n;++i)colors.push_back(colors[i]);
        vector<int>issues;
        for(int i=0;i<k-1;++i){
            if(colors[i]==colors[i+1])issues.push_back(i);
        }
        for(int i=0;i<n;++i){
            auto it = lower_bound(begin(issues),end(issues),l);
            if(it==end(issues))ans++;
            if(colors[r+1]==colors[r])issues.push_back(r);
            l++;r++;
        }
        return ans;
    }
};
