class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0;i<k-1;i++)
        {
            colors.push_back(colors[i]);
        }
        int ans = 0;
        int res=0;
        int n = colors.size();
        int maxAlternatingLength = 1;
        int currentAlternatingLength = 1;
        for (int i = 1; i < n; i++) {
            if (colors[i] != colors[i-1]) {
                currentAlternatingLength++;
            }
            else {
                // maxAlternatingLength = max(maxAlternatingLength, currentAlternatingLength);
                if(currentAlternatingLength>=k)
                res+=(currentAlternatingLength-k+1);
                currentAlternatingLength = 1;
            }
        }
        // maxAlternatingLength = max(maxAlternatingLength, currentAlternatingLength);
        // ans = max((maxAlternatingLength - k + 1), 0);
        if(currentAlternatingLength>=k)
        res+=(currentAlternatingLength-k+1);
        return res;
        // return ans;
    }
};
