class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size(),cnt=1,x=c[0],ans=0;

        for(int i=1;i<n;i++){
            x^=c[i];
            if(x==1){
                cnt++;
                if(cnt==k){
                    cnt--;
                    ans++;
                }
            } else{
                cnt=1;
            }

            x=c[i];
        }

        if(c[0]!=c[n-1]){
            x=c[n-1];
            cnt=1;
            for(int i=n-2;i>=0;i--){
                x^=c[i];
                if(x==1){
                    cnt++;
                    if(cnt==k-1){
                        ans++;
                        break;
                    }
                } else{
                    break;
                }
                x=c[i];
            }
            if(cnt!=k-1) cnt++;
            x=c[0];
            for(int i=1;i<min(n,k-1);i++){
                x^=c[i];
                if(x==1){
                    cnt++;
                    if(cnt==k){
                        cnt--;
                        ans++;
                    }
                } 
                else{
                    break;
                }
                x=c[i];
            }


        }

        return ans;
    }
};
