class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int last=0;
        int j=0;
        int res=0;
        for(int i=0;i<n;i++){
            for(j=max(i,j);j<i+k-1;j++){
                if(colors[(j+1)%n]==colors[j%n]){
                    break;
                }
            }
            if(j==i+k-1){
                res++;
            }
        }
        return res;
    }
};
