class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> colors2=colors;
        for(auto &e:colors){
            colors2.push_back(e);
        }
        int n=colors2.size();
        vector<int> dp(n);
        int res=0;
        for(int i=0;i<n;++i){
            dp[i]=1;
            if(i&&colors2[i]!=colors2[i-1]){
                dp[i]+=dp[i-1];
            }
            if(i>=colors.size()){
                if(dp[i]>=k)++res;
            }
        }
        return res;
    }
};
