class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        n=len(a)
        if k>n:
            return 0
        cnt,pair=0,0
        for i in range(k-1):
            if a[i]!=a[i+1]:
                pair+=1
        if pair==k-1:
            cnt+=1
        for i in range(1,n):
            if a[i-1]!=a[(i-1+1)%n]:
                pair-=1
            if a[(i+k-2)%n]!=a[(i+k-1)%n]:
                pair+=1
            if pair==k-1:
                cnt+=1
        return cnt
