            int[] ans = {0};
            int i = 0;
            int j = 1;

            while (i < n && j - k < n) {
                int z = j;
                int right = j % n;

                while ((nums[right] != nums[((right - 1 + n) % n)]) && (z < n + k - 1)) {
                    z++;
                    right = z % n;
                }

                if(z - i - k + 1 > 0) {
                    ans[0] += z - i - k;
                    ans[0]++;
                }

                j = z + 1;
                i = z;
            }

            return ans[0];
        }
