class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int l=0;
        int x=1;
        for(int i=0;i<colors.size()-1;i++){
            if(colors[i]!=colors[i+1]){
                x++;
            }
            else{
                x=1;
            }
            if(x>=k){
                l++;
            }
        }
        return l;
    }
};
