class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();
        nums.insert(nums.end(),nums.begin(),nums.end());

        int s=0,cnt=0;
        for(int e=1;e<2*n;e++){
            if(nums[e]==nums[e-1]){
                s=e;
                continue;
            }

            if(e-s+1==k and s<n){
                cnt++;
                s++;
            }
        }
        return cnt;
    }
};
