class Solution {
    public int numberOfAlternatingGroups(int[] colors, int groupSize) {
        int totalColors = colors.length;
        int validGroupCount = 0;

        if (groupSize == 1) {
            return totalColors;
        }

        int currentAlternatingLength = 1;
        int currentIndex = 1;
        while (currentIndex < totalColors + groupSize - 1) {
            int currentColorIndex = currentIndex % totalColors;
            int previousColorIndex = (currentIndex - 1) % totalColors;

            if (colors[currentColorIndex] != colors[previousColorIndex]) {
                ++currentAlternatingLength;
            } else {
                currentAlternatingLength = 1;
            }

            if (currentAlternatingLength >= groupSize) {
                ++validGroupCount;
            }

            ++currentIndex;
        }

        return validGroupCount;
    }
}
