class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size(),cnt=0,i,j;
        vector<int> a(n+n);
        for(i=0;i<n+n;i++) a[i]=v[i%n];
        vector<int> p(n+n);
        p[0]=0;
        for(int i=1;i<(n+n);i++){
            p[i]=p[i-1];
            if(a[i]==a[i-1]) p[i]++;
        }for(i=0;i<n;i++){
            if(p[i+k-1]==p[i]) cnt++;
        }return cnt;
    }
};
