class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        for i in range(k + 1):
            colors.append(colors[i % n])

        alternating = 0
        for i in range(k - 1):
            alternating += int(colors[i] != colors[i + 1])
        ans = 0
        for i in range(n):
            if alternating == k - 1:
                ans += 1
            alternating -= int(colors[i] != colors[i + 1])
            alternating += int(colors[i + k - 1] != colors[i + k])
        return ans
