class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
             int n = colors.size();
        int ans = 0,len= 1,i= 0,j=1;
        while(j < n+k-1){
            if(colors[j%n] != colors[(j-1)%n]){
                len++;
            }
            else{
                len = 1;
                i = j;
            }
            if(len == k){
                ans++;
                len--;
                i++;
            }
            j++;
        }
        return ans;

    }
};
