public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        if (colors.Length < k) return 0;
        var n = colors.Length;
        int count = 0;
        var l = 0;
        var r = 0;
        for (int i = 0; i < n + k; i++) {
            if (l >= n) break;
            if (colors[i%n] != colors[(i + 1)%n]) {
                r = i + 1;
            }
            else {
                l = i + 1;
                continue;
            }

            if (r - l + 1 == k) {
                count++;
                l++;
            }
        }

        return count;
    }
}
