class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        int n = colors.size(), cnt = 0, ans = 0;
        colors.insert(colors.end(), colors.begin(), colors.end());
        for (int l = 0, r = 0; l < n; l++) {
            while (r < l + k - 1) cnt += colors[r + 1] != colors[r], r++;
            if (r - l != k - 1) break;
            if (cnt == k - 1) ans++;
            cnt -= colors[l] != colors[l + 1];
        }
        return ans;
    }
};
