class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        // 滑动窗口 但是有环
        int ans = 0, n = colors.length, left = 0, right = 0;
        while (left < n) {
            // 找到一个长度为k的有效数组
            while (right - left + 1 != k) {
                if(colors[(right + 1) % n] == colors[right % n]) {
                    left = right + 1;
                }
                if(left >= n) return ans;
                right++;
            }
            ans++;
            left++;
        }
        return ans;
    }
}
