class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++) {
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        vector<int> v(n,1);
        for(int i=1;i<n;i++) {
            if(colors[i-1]!=colors[i]) {
                v[i]=v[i-1]+1;
                v[i-1]=0;
            }
        }
        int res = 0;
        for(int i=0;i<n;i++) {
            if(v[i]>=k) {
                res=res+v[i]-k+1;
            }
        }
        return res;
    }
};
