class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i, ans = 0, len = 1;
        for(i=0;i<k-1;i++)  colors.push_back(colors[i]);
        int n = colors.size();
        for(i=1;i<n;i++){
            if(colors[i] != colors[i-1])    len++;
            else                            len = 1;
            if(len >= k)    ans++;
        }
        return ans;
    }
};
