class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        bool flag = colors[0];
        int ans = 0;
        int len = 0;
        for(int i=0; i<n; i++) {
            len++;
            if(colors[i] != flag){
                len = 1;
                flag = colors[i];
            }
            if(len >= k)
                ans++;
            flag = !flag;
        }
        for(int i=0; i<k-1; i++) {
            len++;
            if(colors[i] != flag){
                break;
                flag = colors[i];
            }
            if(len >= k)
                ans++;
            flag = !flag;
        }
        return ans;
    }
};
