class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for (int i=0; i<k; i++) a.push_back(a[i]);
        int n=a.size(), ans=0;
        for (int i=1, j=0; i+1<n; i++) {
            if (a[i] == a[i-1]) j=i;
            if (i+1-j == k) j++, ans++;
        }
        return ans;
    }
};
