class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int l = 0;
        int n = colors.size();
        for (int i = 1; i < k - 1; ++i) {
            if (colors[i] == colors[i - 1]) {
                l = i;
            }
        }
        int ans = 0;
        for (int i = k - 1, cnt = 0; cnt < n; ++i, ++cnt) {
            int idx = i % n;
            int pre_idx = (i - 1 + n) % n;
            if (colors[idx] == colors[pre_idx]) {
                l = i;
            }
            if (i - l + 1 >= k) {
                // cout << l << " " << i   << endl;
                ++ans;
            }
        }
        return ans;
    }
};
