class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {

        a.insert(a.end(), a.begin(), a.begin()+k-1);
        int r=0, n=1;

        for(int i=1;i<a.size();i++)
        {
            a[i]!=a[i-1]?++n:n=1;
            if(n>=k) ++r;

        }
        return r;

    }
};
