class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k - 1; i++) 
            colors.push_back(colors[i]);

        vector<int> v(colors.size(), 0);
        for(int i = 0; i + 1 < colors.size(); i++) {
            if(colors[i] == colors[i + 1]) {
                v[i] = 1;
            }
        }

        int s = 0;
        for(int i = 0; i < k - 1; i++) s += v[i];

        int count = 0;
        for(int i = 0; i + k - 1 < v.size(); i++) {
            if(s == 0) count++;
            s += v[i + k - 1];
            s -= v[i];
        }

        return count;
    }
};
