class Solution {
public:
    int query(int i , int j,vector<int> &pre,vector<int> &pre1,int n){

        if(j<n){


        if(i==0){
            return pre[j];
        }

        return pre[j]-pre[i-1];

        }
        int sum = 0;
        j = j %n;
        sum+=pre1[j];

        sum+=pre[n-1]-pre[i-1];

        return sum;

    }

    bool isit(int req1,int req2 , int r , int b){

        if(req1==r and req2==b){
            return true;
        }

        if(req1==b and req2==r){
            return true;
        }

        return false;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        vector<int> red_odd(n,0) , blue_odd(n,0) ,red_even(n,0) , blue_even(n,0);

        for(int i = 0 ; i < n ; i++){

            if(i%2){


                if(colors[i]==0){
                    red_odd[i]=1;
                }else{
                    blue_odd[i]=1;
                }
            }else{

                if(colors[i]==0){
                    red_even[i]=1;
                }else{
                    blue_even[i]=1;
                }

            }
        }


        for(int i =1 ; i<n;i++){
            red_even[i] = red_even[i-1]+red_even[i];
            blue_even[i] = blue_even[i-1]+blue_even[i];
            red_odd[i] = red_odd[i-1]+red_odd[i];
            blue_odd[i] = blue_odd[i-1]+blue_odd[i];
        }

        for(int i = 0 ; i<n;i++){
            int first = i ;
            int second = i+k-1;



            int req1 = k/2;
            int req2 = k/2;
            if(k%2)
                req1++;


            // red even and blue odd 
            int r1,b1,r2,b2;
            if(n%2==1){


            r1 = query(first,second,red_even,red_odd,n);
            b1 = query(first,second,blue_odd,blue_even,n);
            r2 = query(first,second,red_odd,red_even,n);
            b2 = query(first,second,blue_even,blue_odd,n);

            }else{

                 r1 = query(first,second,red_even,red_even,n);
                 b1 = query(first,second,blue_odd,blue_odd,n);
                 r2 = query(first,second,red_odd,red_odd,n);
                 b2 = query(first,second,blue_even,blue_even,n);
            }


            if(isit(req1,req2,r1,b1) or isit(req1,req2,r2,b2)){
                // cout<<first<<" "<<second<<endl;
                // cout<<"r1 "<<r1<<" "<<"b1 "<<b1<<" ";
                // cout<<"r2 "<<r2<<" "<<"b2 "<<b2<<endl;
                ans++;
            }
        }

        //     // red even and blue odd 
        // int r1 = query(3,8,red_even,red_even,n);
        // int b1 = query(3,8,blue_odd,blue_even,n);
        // int r2 = query(3,8,red_odd,red_even,n);
        // int b2 = query(3,8,blue_even,blue_odd,n);

        // for(auto i : red_odd ){
        //     cout<<i<<" ";
        // }
        // cout<<endl;

        // cout<<"r1 "<<r1<<" "<<"b1 "<<b1<<" ";
        // cout<<"r2 "<<r2<<" "<<"b2 "<<b2<<endl;
        return ans;
    }
};
