class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,n=colors.size(),in=-1;
        for(int i=0;i<n;i++)
        {
            if(colors[i]==colors[(i+1)%n])
            {
                in=(i)%n;
                break;
            }
        }
        if(in==-1)return n;
        int cnt=0;
        int lst=in;
        for(int i=(in+1)%n;;i++,i%=n)
        {
            int pre=(i-1+n)%n;
            if(colors[i]!=colors[pre])
            {
                cnt++;
                if(in!=i)continue;
            }
            // cout<<i<<" "<<cnt<<" "<<in<<endl;
            if(cnt-(k-1)>0)ans+=cnt-(k-1);
            cnt=1;
            if(i==in)break;
        }
        return ans;
    }
};
