class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int dp = 1, ans = 0;
        for(int i=1;i<n+k-1;i++){
            int x = colors[i%n];
            int p = colors[(i-1)%n];
            if(x!=p) {
                dp++;
            } else {
                dp = 1;
            }
            if(dp>=k) ans++;
        }
        return ans;
    }
};
