class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();

        for(int i = 0; i<k-1; i++){
            colors.push_back(colors[i]);
        }

        int ind = 0, ans = 0;
        while(ind < N){
            int len = 0, currTar = colors[ind];

            while(ind < colors.size() && colors[ind] ==currTar ){
                len++;
                ind++;
                currTar = !currTar;
            }

            cout << ind << " " << len << endl;

            ans += max(0, len-k+1);
        }

        return ans;
    }
};
