class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& ar, int k) {
        int co = 0;
        vector<int> br;
        for(int i=1;i<=ar.size();++i)
            br.push_back(ar[i%ar.size()]^ar[i-1]);

        int ctr = 0;
        for(int i=0;i<k-1;++i)
            ctr+=br[i];
        if(ctr == k-1)
            ++co;
        for(int i=k-1;i-k+2<br.size();++i)
        {
            ctr-=br[(i-k+1)%br.size()];
            ctr+=br[i%br.size()];
            if(ctr==k-1)
                ++co;//, cout<<i<<" ";
        }
        //cout<<endl;

        return co;
    }
};
