class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
           int n = colors.size();
        if (k > n) 
            return 0;
        int count = 0;
        int alternatingCount = 1;
        for (int i = 1; i < n + k - 1; ++i) {
            if (colors[i % n] != colors[(i - 1) % n]) {
                alternatingCount++;
                if (alternatingCount == k) {
                    count++;
                    alternatingCount--;
                }
            } else {
                alternatingCount = 1;
            }
        }
        return count;
    }
};
