class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans = 0;
        int n = colors.length;
        int prev = -1;
        for(int i = 0; i<2*n; i++){
            if(prev == -1 || prev == colors[i%n]){
                int cp = -1;
                if(i < n){
                    for(int j = i; j<i+k; j++){
                        int idx = j%n;
                        if(cp == colors[idx]){
                            cp = -1;
                            i = j-1;
                            break;
                        }
                        cp = colors[idx];
                    }
                    if(cp != -1){
                        // System.out.println("start " + i);
                        ans++;
                        i = i+k-1;
                        prev = cp;
                    }else{
                        prev = -1;
                        continue;
                    }
                }else{
                    break;
                }
            }else if((i-k) < n-1){
                ans++;
                prev = colors[i%n];
                // System.out.println("end " + i);
            }else{
                break;
            }
        }
        return ans;
    }
}
