class Solution {
public:

    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size();
        for(int i=0;i<min(n,k-1);i++){
            col.push_back(col[i]);
        }
        int ans = 0;
        int cnt = 1;
        for(int i=0;i<col.size()-1;i++){
            if(col[i] == col[i+1]){
                cnt = 1;

            }
            else{
                cnt++;
                if(cnt  >= k){
                    ans++;
                }
            }
        }

        return ans;
    }
};
