class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> dp(n, 0);

        int ans=0;

        int l=0;
        while(l<n)
        {
            int r=l+1;
            while(r<n+k-1 && colors[r%n]!=colors[(r-1)%n])
            {       
                ++r;
            }
            if(r-l>=k) ans+=r-l-k+1;
            l=r;
        }

        return ans;
    }
};
