class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        std::vector<int> q=colors;
        for(auto x:colors) q.push_back(x);
        int n=q.size();
        std::vector<int> f(n+2,0);
        f[1]=1;
        for(int i=2;i<=n;i++){
            int v=q[i-1],la=q[i-2];
            if(v!=la) f[i]=f[i-1]+1;
            else f[i]=1;
        }
        int cnt=0;
        for(int i=k;i<=colors.size()+k-1;i++){
            if(f[i]>=k) cnt++;
        }
        return cnt;
    }
};
