class Solution {
    public int numberOfAlternatingGroups(int[] Arr, int k) {
        int n = Arr.length; 

        int res = 0; 

        int len = 1; 
        for(int i = 1; i < n + k - 1; i++) { 

        	int curr = i; 
            int prev = i - 1; 

            curr = curr % n;
            prev = prev % n;

            if (Arr[curr] != Arr[prev]) { 
                len++; 
            } else { 
                len = 1; 
            } 

            if (len >= k) { 
                res++; 
            } 
        } 

       return res;
    }
}
