class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int start=0;
        int end=0;
        int last=-1;
        int ans=0;
        vector<int> vis(colors.size(),0);
        while(end<colors.size()){
            if(last==-1){
                last=colors[end];
            }
            else if(last==colors[end]){
                start=end;
                last=-1;
                continue;
            }
            else{
                last=colors[end];
            }
            if(end-start+1==k){
                ans++;
                start++;
            }
            end++;
        }
        if(start==colors.size()){
            return ans;
        }
        cout<<start<<endl;

        end=start;
        last=-1;
        while(start<colors.size()){
            if(last==-1){
                last=colors[end];
            }
            else if(last==colors[end]){
                if(end>start){
                    start=end;
                    last=-1;
                    continue;
                }
                else{
                    break;
                }
            }
            else{
                last=colors[end];
            }
            if((end>start && end-start+1==k) || (start>end && colors.size()-start+end+1==k)){
                ans++;
                cout<<"control"<<endl;
                start++;
            }
            end++;
            end%=colors.size();
        }
        return ans;
    }
};
