class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0;
        vector<int> pre(n,0);
        for(int i=0;i<n;i++){
            int a=i+1, b=i-1;
            a%=n; a+=n; a%=n;
            b%=n; b+=n; b%=n;
            if(colors[i]!=colors[a] && colors[i]!=colors[b]){
                pre[i]=1;
            }
        }
        // int ans=0;
        vector<int> pre1=pre;
        for(int i=1;i<n;i++) pre[i]+=pre[i-1];

        for(int i=0;i<n;i++){
            int a=i+k-1;
            a%=n;
            if(a>=i){
                int val=pre[a];
                if(i!=0) val-=pre[i-1];
                if(val>=k-1) ans++;
                else{
                    if(val==k-2 && pre1[i]==0 && pre1[a]==0) ans++;
                }
            }
            else{
                int val=pre[a]+pre[n-1];
                if(i!=0) val-=pre[i-1];
                if(val>=k-1) ans++;
                else{
                    if(val==k-2 && pre1[i]==0 && pre1[a]==0) ans++;
                }
            }
        }
        return ans;
    }
};
