class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {

        int ans=0;int n=arr.length;
			int one=0;int[]brr=new int[2*n];
			for(int i=0;i<n;i++){
				brr[i]=arr[i];
				brr[i+n]=arr[i];
			}
			long[]crr=new long[2*n];
			for(int i=0;i<2*n-1;i++){
				if(brr[i]==brr[i+1])crr[i]=Integer.MIN_VALUE;
			}

			for(int i=1;i<2*n;i++)crr[i]+=crr[i-1];

			for(int i=0;i<n;i++){
				int l=k+i-1;
				long d=crr[l-1];
				if(i>0)d-=crr[i-1];
				if(d>=0){
					ans++;
				}
			}
			return ans;
    }
}
