class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> st(n,0);
        for(int i=0;i<n;i++){
            if(colors[i]!=colors[(i-1+n)%n]){
                st[i]=1;
            }
        }
        int i=0;
        int l=0;
        int j=k-1;
        for(int i=0;i<=k-1;i++){
            if(st[i]==0 && i!=0){
                l=i;
            }
        }
        int ans=0;
        if(l==0){
            ans++;
        }
        // cout<<ans<<endl;
        while(i<n){
            // cout<<i<<endl;
            if(l!=i){
                if(l<i){return ans;}
                i=l;
                int c=j-i+1;
                if(j<i){
                    c=n-i+j+1;
                }
                while(c<k){
                    j=(j+1)%n;
                    c++;
                    if(st[j]==0){
                        l=j;
                    }
                }
                if(l==i){ans++;}
            }
            else{
                l=(l+1)%n;
                if(i==n-1){return ans;}
                i=(i+1)%n;
                j=(j+1)%n;
                if(st[j]){
                    ans++;
                }
                else{
                    l=j;
                }
            }
        }
        return ans;
    }
};
