class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i =0;i<k-1;i++){
            v.push_back(v[i]);
        }
        int len=1;
        int ans=0;
        for(int i=1;i<v.size();i++){
            if(v[i]!=v[i-1]){
                // cout << i << " ";
                len++;
                // cout << len << "\n";
            }
            else{
                if(len>=k)
                ans+=max(len-k+1,0);
                len=1;
            }
        }

        if(len>=k)
        ans+=max(len-k+1,0);
        return ans;
    }
};
