#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i =0;i<k-1;i++) colors.push_back(colors[i]);
        ll n = colors.size(),ans = 0,cnt = 1;
        for(int i =1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                ++cnt;
            }else{
                ans+= max(0LL,1+cnt-k);
                cnt = 1;
            }
        }
        ans+=max(0LL,1+cnt-k);
        return ans;
    }
};
