class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        cnt = ans = 0
        for i in range(1,n+k-1):
            if colors[i] != colors[i-1]: cnt += 1
            if i >= k:
                if colors[i-k] != colors[i-k+1]: cnt -= 1
            if cnt == k-1: ans += 1
        return ans
