class Solution(object):
    def numberOfAlternatingGroups(self, color_list, k):
        n = len(color_list)
        start = 0
        end = 1
        group_count = 0

        while end < 2 * n:
            if color_list[end % n] != color_list[(end - 1) % n]:
                if (end - start + 1) == k:
                    group_count += 1
                    start += 1
                    if start >= n:
                        break
                end += 1
            else:
                start = end
                if start >= n:
                    break
                end += 1

        return group_count
