class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0;

        vector<int> temp(n, 0);
        for (int i = 0; i < n; i++) {
        if (colors[i] != colors[(i + 1) % n]) {
            temp[i] = 1;
        }
     }

    int ans = 0;
    int x = 0;

    for (int i = 0; i < k - 1; i++) {
        x += temp[i];
    }

    for (int i = 0; i < n; i++) {
        if (x == k - 1) {
            ans++;
        }
        x -= temp[i];
        x += temp[(i + k - 1) % n];
    }

    return ans;
    }
};
